import React, { useContext, useEffect, useState } from 'react'
import {
  ImageContextType as ContextType, 
  defaultImageContext
} from '../types/latestInspectionImageContextType'
import { useLastImageGet } from '../hooks/useLastImageGet'
import { TSector } from '../../../types/sectorType'
type ImageContextProps = {
  children: React.ReactNode
}

const ImageContext =
 React.createContext<ContextType>(defaultImageContext)

export function useImage()  {
  return useContext(ImageContext)
}
export const LatestInspectionImageContextProvider: 
React.FC<ImageContextProps> = (
  {children}
) => {
  const [sector, setSector] = useState<TSector>()
  const updateSector = (updatedSector: TSector | undefined) => {
    console.log('updateSector', updatedSector)
    setSector(updatedSector)
  }


  const {
    lastImageString, 
    lastImageStatus, 
    lastImageUpdatedAt,
    updateSectorId, 
    lastImageRefetch
  } =  useLastImageGet(4000) 

  const [image, setImage] = useState(
    {...defaultImageContext,updateSector: updateSector}
  )


  useEffect(() => {
    console.log('sector updated')
    if(sector === undefined && 
      image.selectedSector !== undefined) {setImage({
      ...defaultImageContext, updateSector: updateSector
    })}
    // eslint-disable-next-line @stylistic/max-len
    // if(sector !== undefined && sector !== image.selectedSector) {
    //   updateSectorId(sector.id)
    //   lastImageRefetch()
    // }
  }, [sector, setImage, updateSectorId])

  useEffect(() => {
    console.log('image fetched')
    if (lastImageStatus === 'success'){
      setImage({
        latestImage: lastImageString,
        selectedSector: sector,
        updateSector: updateSector
      })
    }
  },[lastImageStatus, lastImageString, updateSector, setImage])

  return (
    <>
      <ImageContext.Provider value={image}>
        {children}
      </ImageContext.Provider>
    </>
  )
}
