import { Button, Space, Spin } from 'antd'
import React from 'react'
import { useVerifyInspection } from '../hooks/useVerififyInspection'

type VerifyButtonContainerProps = {
  inspectionId: number
  onClick: Function
}
export const VerifyButtonContainer: React.FC<
VerifyButtonContainerProps> = ({
  inspectionId, 
  onClick
}) => {
  const { uncheckInspection, defectInspection } =
    useVerifyInspection(inspectionId)

  return (
    <Space direction="horizontal">
      Установить статус
      {uncheckInspection.status === 'loading' ||
      defectInspection.status === 'loading' ? 
        <Spin />
        : 
        <>
          <Button
            onClick={() => 
            {
              uncheckInspection.mutate()
              onClick()
            }}
          >
            Не проверено
          </Button>
          <Button
            onClick={() => {
              defectInspection.mutate()
              onClick(true)
            }}
          >
            Брак
          </Button>
        </>
      }
    </Space>
  )
}
