import { Button, Form, Select, Space, Spin } from 'antd'
import React from 'react'
import { useSectorGet } from '../../../hooks/useSectorGet'
import mapSectorIds from '../../../utils/sectorMapper'
import { TSector } from '../../../types/sectorType'
import {
  ImageContextType
} from '../types/latestInspectionImageContextType'

import { 
  useImage, 
  useUpdateImage 
} from '../context/latestInspectionImageContext'
import { SectorInput } from '../../../components/sectorInput'
export const SectorPicker: React.FC = () => {

  const [pickedSector, setPickedSector] = React.useState<TSector>() 

  const updateImageData = useUpdateImage()
  const imageData = useImage()

  const handleDataInput = (
    updates: Partial<ImageContextType>
  ) => {
    updateImageData({
      ...imageData,
      ...updates
    })
  }
  return <Form>
    <Space direction='vertical'>
      <SectorInput
        isMultiple={false}
        allowClear={false}
        onChange={
          selectedSector => 
            handleDataInput(
              {selectedSector: selectedSector as TSector}
            )
        }
      />
      <Button
        disabled={pickedSector === undefined}>
        Подтвердить
      </Button>
    </Space>
  </Form>
}
