import { Button, Form, Select, Space, Spin } from 'antd'
import React from 'react'
import { useSectorGet } from '../../../hooks/useSectorGet'
import mapSectorIds from '../../../utils/sectorMapper'
import { TSector } from '../../../types/sectorType'
import {
  ImageContextType
} from '../types/latestInspectionImageContextType'

import { 
  useImage, 
  useUpdateImage 
} from '../context/latestInspectionImageContext'
export const SectorPicker: React.FC = () => {
  const {sectors, sectorsStatus} = useSectorGet()

  const updateImageData = useUpdateImage()
  const imageData = useImage()

  const handleDataInput = (
    updates: Partial<ImageContextType>
  ) => {
    updateImageData({
      ...imageData,
      ...updates
    })
  }
  return <Form>
    <Space>
      {sectorsStatus == 'success' 
        ? <Select
          options={mapSectorIds(sectors as TSector[])}
        />
        : <Spin />
      }
      <Button disabled={sectorsStatus !== 'success'}>
        Подтвердить
      </Button>
    </Space>
  </Form>
}
