import React, { useContext, useEffect, useState } from 'react'
import {
  ImageContextType as ContextType, 
  defaultImageContext
} from '../types/latestInspectionImageContextType'
import { useLastImageGet } from '../hooks/useLastImageGet'
import { TSector } from '../../../types/sectorType'
type ImageContextProps = {
  children: React.ReactNode
}

const ImageContext =
 React.createContext<ContextType>(defaultImageContext)

export function useImage()  {
  return useContext(ImageContext)
}
export const LatestInspectionImageContextProvider: 
React.FC<ImageContextProps> = (
  {children}
) => {
  const [image, setImage] = useState(defaultImageContext)

  const [sector, setSector] = useState<TSector>()
  const {
    lastImageString, 
    updateSectorId, 
    lastImageStatus, 
    lastImageUpdatedAt
  } =  useLastImageGet(4000) 

  const updateSector = (updatedSector: TSector | undefined) => {
    if (updatedSector === undefined) {
      setSector(undefined)
      return
    }
    setSector(updatedSector)
  }

  useEffect(() => {
    console.log(sector)
    if(sector === undefined) setImage(defaultImageContext)
    else updateSectorId(sector.id)
  }, [sector, setImage])

  useEffect(() => {
    if (lastImageStatus === 'success'){
      setImage({
        latestImage: lastImageString,
        selectedSector: sector,
        updateSector: updateSector
      })
    }
  },[lastImageStatus, lastImageString])

  return (
    <>
      <ImageContext.Provider value={image}>
        {children}
      </ImageContext.Provider>
    </>
  )
}
