import { useQuery } from 'react-query'
import { getLastImage } from '../services/getLastImage'
import { useEffect, useState } from 'react'
import { useDebounce } from '../../../hooks/useDebounce'
import { TSector } from '../../../types/sectorType'

export const useLastImageGet = (
  refetchDelay: number
) => {
  const debounce = useDebounce()

  const [sector, setSector] = useState<TSector>()
  const updateSectorId = (sectorId: TSector) => {
    setSector(sector)
    refetch()
  }

  const {data, status, refetch, dataUpdatedAt} = useQuery<string, string>(
    {queryFn: () => sector === undefined ? '' : getLastImage(sector.id)}
  )

  useEffect(() => {
    if (status === 'success')
      debounce(() => {refetch()}, refetchDelay)
  }, [status, refetch])

  return {
    lastImageString: data, 
    lastImageStatus: status, 
    lastImageUpdatedAt: dataUpdatedAt,
    selectedSector: sector,
    lastImageRefetch: refetch,
    updateSectorId: updateSectorId,
  }
}
